V=0;[[ $2 == "-v" ]]&&V=1;REQ_HEADERS=$1;curl https://www.example.com/>example.html;(($V==1))&&((($?==0))&&echo "Fetched-example.com"||echo "Error-fetching-example.com");curl -i http://ip.jsontest.com/;(($V==1))&&((($?==0))&&echo "Fetched-IP"||echo "Error-fetching-IP");arr=(${REQ_HEADERS//,/ });s=$(printf ".\"%s\", " "${arr[@]}");s=${s::-2};curl http://headers.jsontest.com/|jq "$s";(($V==1))&&((($?==0))&&echo "Fetched-headers"||echo "Error-fetching-headers");f=(JSONData/*);for((i=0;i<${#f[@]};i++));do val=$(curl -d "json=`cat ${f[i]}`" http://validate.jsontest.com/|jq .validate);((${#val}==4))&&(echo ${f[i]##*/}>>valid.txt)||(echo ${f[i]##*/}>>invalid.txt);done